	private void jCheckBox9MouseReleased(java.awt.event.MouseEvent evt) {
		Interests newi = new Interests();
		ProfileDAO dao = new ProfileDAO();
		Transaction dx = dao.getSession().beginTransaction();
		Profile ma = dao.findById(FirstPage.id);
		int i;
		for (i = 0; i < ma.getInterests().size(); i++) 
			if(ma.getInterests().get(i).getName().equals(newi.getjCheckBox9().getText())){
				dx.commit();
				dao.getSession().close();
				RemoveInterest(FirstPage.id, "Business");
				return;
	}
		dx.commit();
		dao.getSession().close();
		AddInterest(FirstPage.id, "Business");
	}

