	static void AddInterest(Integer profileid, String interestname) {
		ProfileDAO dao = new ProfileDAO();
		Transaction dx = dao.getSession().beginTransaction();
		Profile a = dao.findById(profileid);

		List<Interest> newpis = a.getInterests();
		Interest newi = new Interest();
		newi.setName(interestname);

		newpis.add(newi);

		dao.attachClean(a);
		dx.commit();
		dao.getSession().close();

	}
	static void RemoveInterest(Integer profileid, String interestname) {
		ProfileDAO dao = new ProfileDAO();
		Transaction dx = dao.getSession().beginTransaction();
		Profile a = dao.findById(profileid);
	//	Interest found;
		int i;
		List<Interest> newpis = a.getInterests();
		for(i=0;i<newpis.size();i++)
			if(newpis.get(i).getName().equals(interestname))
				newpis.remove(i);
		
		dao.attachClean(a);
		dx.commit();
		dao.getSession().close();

	}
	
	

